      ['skills_id' => 'BR00EF08LI15', 'property_name' => 'Práticas de Linguagem (Eixo)', 'property_value' => 'Eixo conhecimentos linguísticos'],
      ['skills_id' => 'BR00EF08LI15', 'property_name' => 'Unidades Temáticas', 'property_value' => 'Gramática'],
